import Grid from '@material-ui/core/Grid';
import Avatar from '@material-ui/core/Avatar';
import SyncIcon from '@material-ui/icons/Sync';
import Fab from '@material-ui/core/Fab';
import PersonIcon from '@material-ui/icons/Person';
import { makeStyles } from '@material-ui/core/styles';
import React, { useState } from 'react';

const teamMembers = [
  { name: 'Taylor Clark', img: 'https://avataaars.io/?avatarStyle=Circle&topType=LongHairNotTooLong&accessoriesType=Prescription02&hairColor=Blonde&facialHairType=Blank&clotheType=Hoodie&clotheColor=Heather&eyeType=Squint&eyebrowType=Default&mouthType=Smile&skinColor=Pale' },
  { name: 'Delvin Hall', img:'https://avataaars.io/?avatarStyle=Circle&topType=NoHair&accessoriesType=Blank&facialHairType=Blank&clotheType=Hoodie&clotheColor=Black&eyeType=Side&eyebrowType=UpDown&mouthType=Serious&skinColor=DarkBrown' },
  { name: 'Nishant Seth', img: 'https://avataaars.io/?avatarStyle=Circle&topType=ShortHairShortRound&accessoriesType=Blank&hairColor=BrownDark&facialHairType=Blank&clotheType=Hoodie&clotheColor=Blue02&eyeType=Default&eyebrowType=RaisedExcitedNatural&mouthType=Smile&skinColor=Brown' },
  { name: 'Chad Gabrysch', img: 'https://avataaars.io/?avatarStyle=Circle&topType=ShortHairShortRound&accessoriesType=Prescription02&hairColor=Auburn&facialHairType=Blank&clotheType=Hoodie&clotheColor=White&eyeType=Default&eyebrowType=RaisedExcitedNatural&mouthType=Smile&skinColor=Light' },
  { name: 'Chris Soloman', img: 'https://avataaars.io/?avatarStyle=Circle&topType=ShortHairShortFlat&accessoriesType=Blank&hairColor=Brown&facialHairType=Blank&clotheType=Hoodie&clotheColor=Gray01&eyeType=Happy&eyebrowType=RaisedExcitedNatural&mouthType=Smile&skinColor=Light' },
  { name: 'Linda Stewart', img: 'ttps://avataaars.io/?avatarStyle=Circle&topType=LongHairNotTooLong&accessoriesType=Prescription02&hairColor=Black&facialHairType=Blank&clotheType=Hoodie&clotheColor=PastelOrange&eyeType=Happy&eyebrowType=RaisedExcitedNatural&mouthType=Smile&skinColor=DarkBrown' },
  { name: 'Cris Sakae', img: 'https://avataaars.io/?avatarStyle=Circle&topType=LongHairNotTooLong&accessoriesType=Prescription02&hairColor=Black&facialHairType=Blank&clotheType=Hoodie&clotheColor=PastelRed&eyeType=Happy&eyebrowType=RaisedExcitedNatural&mouthType=Smile&skinColor=Pale' },
  { name: 'Kristin Erikson', img: 'https://avataaars.io/?avatarStyle=Circle&topType=LongHairStraight&accessoriesType=Prescription02&hairColor=Auburn&facialHairType=Blank&clotheType=Hoodie&clotheColor=PastelGreen&eyeType=Squint&eyebrowType=Default&mouthType=Smile&skinColor=Light' },
  { name: 'Brian Denholm', img: 'https://avataaars.io/?avatarStyle=Circle&topType=ShortHairShortFlat&accessoriesType=Wayfarers&hairColor=Auburn&facialHairType=Blank&clotheType=Hoodie&clotheColor=PastelBlue&eyeType=Surprised&eyebrowType=RaisedExcitedNatural&mouthType=Tongue&skinColor=Light' },
  { name: 'Jourdan McClish', img: 'https://avataaars.io/?avatarStyle=Circle&topType=LongHairStraight&accessoriesType=Blank&hairColor=BlondeGolden&facialHairType=Blank&clotheType=Hoodie&clotheColor=Black&eyeType=Wink&eyebrowType=Default&mouthType=Smile&skinColor=Light' },
];

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    '& > *': {
      margin: theme.spacing(1),
    },
  },
  small: {
    width: theme.spacing(3),
    height: theme.spacing(3),
  },
  large: {
    width: theme.spacing(7),
    height: theme.spacing(7),
  },
  outerSpan: {
    padding: theme.spacing(2),
    margin: 'auto',
    maxWidth: 500,
    textAlign: 'center'
  },
  App: {
    backgroundColor: '#282c34',
    minHeight: '100vh',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white'
  }
}));

const App = () => {
  const classes = useStyles();
  const [selectedTeamMember, setSelectedTeamMember] = useState(null);
  const randomize = () => {
    var random = teamMembers[Math.floor(Math.random() * teamMembers.length)];
    console.log(random)
    setSelectedTeamMember(random);
  }
  return (
    <div className={classes.App}>
      <h1>
        McLaren Randomizer
      </h1>
      <Grid container spacing={3}>
        <Grid container alignItems={'center'} justify={'center'} style={{ width: "100%" }} item xs={12}>
          <Fab variant="extended" onClick={() => randomize()}>
            <SyncIcon className={classes.extendedIcon} />  Randomize
          </Fab>
        </Grid>
        <Grid item xs={12}>
          <Avatar style={{ margin: 'auto', width: "250px", height: "250px", }} alt={selectedTeamMember === null ? '?' : selectedTeamMember.name} src={selectedTeamMember === null ? <PersonIcon /> : selectedTeamMember.img} className={classes.large} />
          <div style={{textAlign: 'center', fontSize: '1.5rem', marginTop: '20px'}}>{selectedTeamMember === null ? 'Who will we choose?' : selectedTeamMember.name}</div>
        </Grid>
      </Grid>
    </div >
  );
}

export default App;
