import './App.css';
import Grid from '@material-ui/core/Grid';
import Avatar from '@material-ui/core/Avatar';
import SyncIcon from '@material-ui/icons/Sync';
import Fab from '@material-ui/core/Fab';
import { makeStyles } from '@material-ui/core/styles';

const teamMembers = [
  { name: 'Taylor Clark', img: 'https://avataaars.io/?avatarStyle=Circle&topType=LongHairNotTooLong&accessoriesType=Prescription02&hairColor=Blonde&facialHairType=Blank&clotheType=Hoodie&clotheColor=Heather&eyeType=Squint&eyebrowType=Default&mouthType=Smile&skinColor=Pale' },
  { name: 'Delvin Hall', img: 'https://avataaars.io/?avatarStyle=Circle&topType=LongHairNotTooLong&accessoriesType=Prescription02&hairColor=Blonde&facialHairType=Blank&clotheType=Hoodie&clotheColor=Heather&eyeType=Squint&eyebrowType=Default&mouthType=Smile&skinColor=Pale' },
  { name: 'Nishant Seth', img: 'https://avataaars.io/?avatarStyle=Circle&topType=LongHairNotTooLong&accessoriesType=Prescription02&hairColor=Blonde&facialHairType=Blank&clotheType=Hoodie&clotheColor=Heather&eyeType=Squint&eyebrowType=Default&mouthType=Smile&skinColor=Pale' },
  { name: 'Chad Gabrysch', img: 'https://avataaars.io/?avatarStyle=Circle&topType=LongHairNotTooLong&accessoriesType=Prescription02&hairColor=Blonde&facialHairType=Blank&clotheType=Hoodie&clotheColor=Heather&eyeType=Squint&eyebrowType=Default&mouthType=Smile&skinColor=Pale' },
  { name: 'Chris Soloman', img: 'https://avataaars.io/?avatarStyle=Circle&topType=LongHairNotTooLong&accessoriesType=Prescription02&hairColor=Blonde&facialHairType=Blank&clotheType=Hoodie&clotheColor=Heather&eyeType=Squint&eyebrowType=Default&mouthType=Smile&skinColor=Pale' },
  { name: 'Linda Stewart', img: 'https://avataaars.io/?avatarStyle=Circle&topType=LongHairNotTooLong&accessoriesType=Prescription02&hairColor=Blonde&facialHairType=Blank&clotheType=Hoodie&clotheColor=Heather&eyeType=Squint&eyebrowType=Default&mouthType=Smile&skinColor=Pale' },
  { name: 'Cris Sakae', img: 'https://avataaars.io/?avatarStyle=Circle&topType=LongHairNotTooLong&accessoriesType=Prescription02&hairColor=Blonde&facialHairType=Blank&clotheType=Hoodie&clotheColor=Heather&eyeType=Squint&eyebrowType=Default&mouthType=Smile&skinColor=Pale' },
  { name: 'Kristin Erikson', img: 'https://avataaars.io/?avatarStyle=Circle&topType=LongHairNotTooLong&accessoriesType=Prescription02&hairColor=Blonde&facialHairType=Blank&clotheType=Hoodie&clotheColor=Heather&eyeType=Squint&eyebrowType=Default&mouthType=Smile&skinColor=Pale' },
  { name: 'Brian Denholm', img: 'https://avataaars.io/?avatarStyle=Circle&topType=LongHairNotTooLong&accessoriesType=Prescription02&hairColor=Blonde&facialHairType=Blank&clotheType=Hoodie&clotheColor=Heather&eyeType=Squint&eyebrowType=Default&mouthType=Smile&skinColor=Pale' },
  { name: 'Jourdan McClish', img: 'https://avataaars.io/?avatarStyle=Circle&topType=LongHairNotTooLong&accessoriesType=Prescription02&hairColor=Blonde&facialHairType=Blank&clotheType=Hoodie&clotheColor=Heather&eyeType=Squint&eyebrowType=Default&mouthType=Smile&skinColor=Pale' },
]
const [selectedTeamMember, setSelectedTeamMember] = null;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    '& > *': {
      margin: theme.spacing(1),
    },
  },
  small: {
    width: theme.spacing(3),
    height: theme.spacing(3),
  },
  large: {
    width: theme.spacing(7),
    height: theme.spacing(7),
  },
  outerSpan: {
    padding: theme.spacing(2),
    margin: 'auto',
    maxWidth: 500,
    textAlign: 'center'
  }
}));

function App() {
  const classes = useStyles();
  const listMembers = teamMembers.map((member) =>
    <span>
      <Avatar style={{ margin: 'auto', width: "25%", height: "25%", }} key={member.name} alt={member.name} src={member.img} className={classes.large} />
      <span>{member.name}</span>
    </span>
  );
  let random;
  if (selectedTeamMember === null) {
    random = <div></div>;
  } else {
    random =
      <Grid item xs={6}>
        <Avatar style={{ margin: 'auto', width: "25%", height: "25%", }} alt={selectedTeamMember.name} src={selectedTeamMember.img} className={classes.large} />
        <span>{selectedTeamMember.name}</span>
      </Grid>
  }
  const randomize = () => {
    var random = teamMembers[Math.floor(Math.random() * teamMembers.length)];
    setSelectedTeamMember(random);
  }
  return (
    <div className="App">
      <h1>
        McLaren Randomizer
      </h1>
      <Grid container spacing={3}>
        <Grid container alignItems={'center'} justify={'center'} style={{ width: "100%" }} item xs={12}>
          <Fab variant="extended" onClick={() => randomize()}>
            <SyncIcon className={classes.extendedIcon} />  Randomize
          </Fab>
        </Grid>
        <Grid item xs={6} className={classes.outerSpan}>
          {listMembers}
        </Grid>
        <Grid item xs={6}>
          <Avatar style={{ margin: 'auto', width: "25%", height: "25%", }} alt={selectedTeamMember.name} src={selectedTeamMember.img} className={classes.large} />
          <span>{selectedTeamMember.name}</span>
        </Grid>
        {random}
      </Grid>
    </div >
  );
}

export default App;
